import React from 'react';
import { Check } from 'lucide-react';

const WeeklyGoals = ({ weeklyGoals, updateGoal, toggleCompletion, setSelectedWeek, selectedQuarter, selectedMonth }) => {
  return (
    <div>
      <h3 className="text-xl font-semibold mt-6 mb-2">Weekly Goals</h3>
      <div className="grid grid-cols-2 gap-4">
        {weeklyGoals.map((week, wIndex) => (
          <div key={wIndex} className="p-2 border rounded">
            <div className="flex items-center mb-2">
              <input
                type="text"
                value={week.goal || ''}
                onChange={(e) => updateGoal('weekly', wIndex, e.target.value)}
                className="flex-grow p-2 border rounded mr-2"
                placeholder={`Week ${wIndex + 1} goal`}
              />
              <button
                onClick={() => toggleCompletion('weekly', [selectedQuarter, selectedMonth, wIndex])}
                className={`p-2 rounded ${week.completed ? 'bg-green-500 text-white' : 'bg-gray-200'}`}
              >
                {week.completed && <Check size={20} />}
              </button>
            </div>
            <button
              onClick={() => setSelectedWeek(wIndex)}
              className="w-full p-2 bg-blue-500 text-white rounded"
            >
              Select
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WeeklyGoals;
