import React from 'react';
import { Check } from 'lucide-react';

const DayLabel = ({ day, goals }) => {
  const isPartiallyFilled = goals.some(goal => goal.trim() !== '');
  const isFullyFilled = goals.every(goal => goal.trim() !== '');
  const isCompleted = goals.every(goal => goal.startsWith('✓ '));

  let className = 'px-2 py-1 rounded-t-md text-center font-semibold';
  if (isCompleted) {
    className += ' bg-green-500 text-white';
  } else if (isFullyFilled) {
    className += ' bg-blue-500 text-white';
  } else if (isPartiallyFilled) {
    className += ' bg-gradient-to-r from-blue-500 to-white text-black';
  } else {
    className += ' bg-gray-200 text-black';
  }

  return <div className={className}>{day}</div>;
};

const DailyGoals = ({ dailyGoals, updateDailyGoal, toggleDailyGoalCompletion, clearDailyGoals }) => {
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

  return (
    <div>
      <h3 className="text-xl font-semibold mt-6 mb-2">Daily Goals</h3>
      {daysOfWeek.map((day, dayIndex) => (
        <div key={day} className="mb-4">
          <DayLabel day={day} goals={dailyGoals.slice(dayIndex * 2, dayIndex * 2 + 2)} />
          <div className="border rounded-b-md p-2">
            {dailyGoals.slice(dayIndex * 2, dayIndex * 2 + 2).map((goal, goalIndex) => (
              <div key={goalIndex} className="flex items-center mb-2">
                <input
                  type="text"
                  value={goal.startsWith('✓ ') ? goal.slice(2) : goal}
                  onChange={(e) => updateDailyGoal(dayIndex * 2 + goalIndex, e.target.value)}
                  className="flex-grow p-2 border rounded mr-2"
                  placeholder={`${day} goal ${goalIndex + 1}`}
                />
                <button
                  onClick={() => toggleDailyGoalCompletion(dayIndex * 2 + goalIndex)}
                  className={`p-2 rounded ${goal.startsWith('✓ ') ? 'bg-green-500 text-white' : 'bg-gray-200'}`}
                >
                  {goal.startsWith('✓ ') && <Check size={20} />}
                </button>
              </div>
            ))}
          </div>
        </div>
      ))}
      <button
        onClick={clearDailyGoals}
        className="mt-2 p-2 bg-red-500 text-white rounded"
      >
        Clear All Daily Goals
      </button>
    </div>
  );
};

export default DailyGoals;
