import React from 'react';
import { Check } from 'lucide-react';

const DailyGoals = ({ dailyGoals, updateDailyGoal, toggleDailyGoalCompletion, clearDailyGoals }) => {
  return (
    <div>
      <h3 className="text-xl font-semibold mt-6 mb-2">Daily Goals</h3>
      {dailyGoals.map((goal, index) => (
        <div key={index} className="flex items-center mb-2">
          <input
            type="text"
            value={goal.startsWith('✓ ') ? goal.slice(2) : goal}
            onChange={(e) => updateDailyGoal(index, e.target.value)}
            className="flex-grow p-2 border rounded mr-2"
            placeholder={`Daily goal ${index + 1}`}
          />
          <button
            onClick={() => toggleDailyGoalCompletion(index)}
            className={`p-2 rounded ${goal.startsWith('✓ ') ? 'bg-green-500 text-white' : 'bg-gray-200'}`}
          >
            {goal.startsWith('✓ ') && <Check size={20} />}
          </button>
        </div>
      ))}
      <button
        onClick={clearDailyGoals}
        className="mt-2 p-2 bg-red-500 text-white rounded"
      >
        Clear Daily Goals
      </button>
    </div>
  );
};

export default DailyGoals;
