import React from 'react';
import { Check } from 'lucide-react';

const QuarterlyGoals = ({ quarterlyGoals, updateGoal, toggleCompletion, setSelectedQuarter, selectedQuarter }) => {
  return (
    <div>
      <h3 className="text-xl font-semibold mb-2">Quarterly Goals</h3>
      {quarterlyGoals.map((quarter, qIndex) => (
        <div key={qIndex} className={`mb-2 p-2 border rounded ${selectedQuarter === qIndex ? 'bg-blue-100' : ''}`}>
          <div className="flex items-center">
            <input
              type="text"
              value={quarter.goal || ''}
              onChange={(e) => updateGoal('quarterly', qIndex, e.target.value)}
              className="flex-grow p-2 border rounded mr-2"
              placeholder={`Q${qIndex + 1} goal`}
            />
            <button
              onClick={() => toggleCompletion('quarterly', [qIndex])}
              className={`p-2 rounded ${quarter.completed ? 'bg-green-500 text-white' : 'bg-gray-200'}`}
            >
              {quarter.completed && <Check size={20} />}
            </button>
            <button
              onClick={() => setSelectedQuarter(qIndex)}
              className="ml-2 p-2 bg-blue-500 text-white rounded"
            >
              Select
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default QuarterlyGoals;
