import React from 'react';

const DailyGoalsForWeek = ({ weekGoals, updateDailyGoal, toggleDailyGoalCompletion, selectedDay, setSelectedDay }) => {
  // Array of days of the week
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

  return (
    <div className="mt-4">
      <h4 className="text-lg font-semibold mb-2">Daily Goals for Selected Week</h4>
      <div className="flex justify-between mb-4">
        {daysOfWeek.map((day, index) => (
          <button
            key={day}
            className={`px-2 py-1 rounded ${selectedDay === index ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
            onClick={() => setSelectedDay(index)}
          >
            {day}
          </button>
        ))}
      </div>
      <div className="space-y-4">
        {[0, 1].map((goalIndex) => (
          <div key={goalIndex} className="flex items-center">
            <input
              type="text"
              value={(weekGoals[selectedDay] && weekGoals[selectedDay][goalIndex] && weekGoals[selectedDay][goalIndex].text) || ''}
              onChange={(e) => updateDailyGoal(selectedDay, goalIndex, e.target.value)}
              className="flex-grow p-2 border rounded mr-2"
              placeholder={`Goal ${goalIndex + 1}`}
            />
            <button
              onClick={() => toggleDailyGoalCompletion(selectedDay, goalIndex)}
              className={`p-2 rounded ${weekGoals[selectedDay] && weekGoals[selectedDay][goalIndex] && weekGoals[selectedDay][goalIndex].completed ? 'bg-green-500 text-white' : 'bg-gray-200'}`}
            >
              âœ“
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DailyGoalsForWeek;
