import React from 'react';

const DailyGoalsForWeek = ({ weekGoals, updateDailyGoal, toggleDailyGoalCompletion }) => {
  // Array of days of the week
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

  return (
    <div className="mt-4">
      <h4 className="text-lg font-semibold mb-2">Daily Goals for Selected Week</h4>
      <div className="flex flex-wrap justify-between">
        {daysOfWeek.map((day, dayIndex) => (
          <div key={day} className="w-full sm:w-1/2 md:w-1/3 lg:w-1/4 xl:w-1/7 p-2">
            <div className="border rounded p-2 h-full">
              <h5 className="font-medium mb-2">{day}</h5>
              <div className="space-y-2">
                {[0, 1].map((goalIndex) => (
                  <div key={goalIndex} className="flex items-center">
                    <input
                      type="text"
                      value={(weekGoals[dayIndex] && weekGoals[dayIndex][goalIndex] && weekGoals[dayIndex][goalIndex].text) || ''}
                      onChange={(e) => updateDailyGoal(dayIndex, goalIndex, e.target.value)}
                      className="flex-grow p-2 border rounded mr-2"
                      placeholder={`Goal ${goalIndex + 1}`}
                    />
                    <button
                      onClick={() => toggleDailyGoalCompletion(dayIndex, goalIndex)}
                      className={`p-2 rounded ${weekGoals[dayIndex] && weekGoals[dayIndex][goalIndex] && weekGoals[dayIndex][goalIndex].completed ? 'bg-green-500 text-white' : 'bg-gray-200'}`}
                    >
                      âœ“
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DailyGoalsForWeek;
