import React from 'react';

const DailyGoalsForWeek = ({ weekGoals }) => {
  // Array of days of the week
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

  return (
    <div className="mt-4">
      <h4 className="text-lg font-semibold mb-2">Daily Goals for Selected Week</h4>
      <div className="space-y-4">
        {daysOfWeek.map((day, index) => (
          <div key={day} className="border rounded p-2">
            <h5 className="font-medium mb-2">{day}</h5>
            <div className="space-y-2">
              {weekGoals.dailyGoals[index].map((goal, goalIndex) => (
                <div key={goalIndex} className="flex items-center">
                  <input
                    type="text"
                    value={goal}
                    onChange={(e) => {
                      // TODO: Implement update function for daily goals
                      console.log(`Update goal for ${day}, index ${goalIndex}`);
                    }}
                    className="flex-grow p-2 border rounded mr-2"
                    placeholder={`Goal ${goalIndex + 1}`}
                  />
                  <button
                    onClick={() => {
                      // TODO: Implement toggle completion function for daily goals
                      console.log(`Toggle completion for ${day}, index ${goalIndex}`);
                    }}
                    className="p-2 bg-gray-200 rounded"
                  >
                    âœ“
                  </button>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DailyGoalsForWeek;
