import React from 'react';
import { Check } from 'lucide-react';

const MonthlyGoals = ({ monthlyGoals, updateGoal, toggleCompletion, setSelectedMonth, selectedMonth, selectedQuarter }) => {
  return (
    <div>
      <h3 className="text-xl font-semibold mt-6 mb-2">Monthly Goals</h3>
      <div className="grid grid-cols-3 gap-4">
        {monthlyGoals.map((month, mIndex) => (
          <div key={mIndex} className={`p-2 border rounded ${selectedMonth === mIndex ? 'bg-blue-100' : ''}`}>
            <div className="flex items-center mb-2">
              <input
                type="text"
                value={month.goal || ''}
                onChange={(e) => updateGoal('monthly', mIndex, e.target.value)}
                className="flex-grow p-2 border rounded mr-2"
                placeholder={`Month ${mIndex + 1} goal`}
              />
              <button
                onClick={() => toggleCompletion('monthly', [selectedQuarter, mIndex])}
                className={`p-2 rounded ${month.completed ? 'bg-green-500 text-white' : 'bg-gray-200'}`}
              >
                {month.completed && <Check size={20} />}
              </button>
            </div>
            <button
              onClick={() => setSelectedMonth(mIndex)}
              className="w-full p-2 bg-blue-500 text-white rounded"
            >
              Select
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MonthlyGoals;
