import React, { useState, useEffect, useCallback, useMemo } from 'react';
import Categories from './components/Categories';
import AnnualGoal from './components/AnnualGoal';
import QuarterlyGoals from './components/QuarterlyGoals';
import MonthlyGoals from './components/MonthlyGoals';
import WeeklyGoals from './components/WeeklyGoals';
import DailyGoals from './components/DailyGoals';
import WorkTimer from './components/WorkTimer';
import { categories, loadGoals, saveGoals } from './utils/goalUtils';
import useLocalStorage from './hooks/useLocalStorage';
import useDarkMode from './hooks/useDarkMode';

const App = () => {
  const [goals, setGoals] = useLocalStorage('goals', loadGoals());
  const [currentCategory, setCurrentCategory] = useState(categories[0]);
  const [selectedQuarter, setSelectedQuarter] = useState(null);
  const [selectedMonth, setSelectedMonth] = useState(null);
  const [selectedWeek, setSelectedWeek] = useState(null);
  const [dailyGoals, setDailyGoals] = useLocalStorage('dailyGoals', Array(14).fill(''));

  const [remainingTime, setRemainingTime] = useState(0);
  const [selectedDuration, setSelectedDuration] = useState(15);
  const [isTimerRunning, setIsTimerRunning] = useState(false);

  const [darkMode, toggleDarkMode] = useDarkMode();

  useEffect(() => {
    saveGoals(goals);
  }, [goals]);

  const updateGoal = useCallback((level, index, value) => {
    setGoals(prevGoals => {
      const newGoals = { ...prevGoals };
      let target = newGoals[currentCategory];

      if (!target) {
        console.error(`Category ${currentCategory} not found in goals`);
        return prevGoals;
      }

      switch (level) {
        case 'annual':
          target.annual = value;
          break;
        case 'quarterly':
          if (target.quarterly[index]) {
            target.quarterly[index] = { ...target.quarterly[index], goal: value };
          }
          break;
        case 'monthly':
          if (selectedQuarter !== null && target.quarterly[selectedQuarter]?.monthly[index]) {
            target.quarterly[selectedQuarter].monthly[index] = {
              ...target.quarterly[selectedQuarter].monthly[index],
              goal: value
            };
          }
          break;
        case 'weekly':
          if (selectedQuarter !== null && selectedMonth !== null && 
              target.quarterly[selectedQuarter]?.monthly[selectedMonth]?.weekly[index]) {
            target.quarterly[selectedQuarter].monthly[selectedMonth].weekly[index] = {
              ...target.quarterly[selectedQuarter].monthly[selectedMonth].weekly[index],
              goal: value
            };
          }
          break;
        default:
          console.error(`Unknown level: ${level}`);
          return prevGoals;
      }

      return newGoals;
    });
  }, [currentCategory, selectedQuarter, selectedMonth, setGoals]);

  const toggleCompletion = useCallback((level, indices) => {
    setGoals(prevGoals => {
      const newGoals = { ...prevGoals };
      let target = newGoals[currentCategory];

      switch (level) {
        case 'quarterly':
          if (target.quarterly[indices[0]]) {
            target.quarterly[indices[0]] = {
              ...target.quarterly[indices[0]],
              completed: !target.quarterly[indices[0]].completed
            };
          }
          break;
        case 'monthly':
          if (target.quarterly[indices[0]]?.monthly[indices[1]]) {
            target.quarterly[indices[0]].monthly[indices[1]] = {
              ...target.quarterly[indices[0]].monthly[indices[1]],
              completed: !target.quarterly[indices[0]].monthly[indices[1]].completed
            };
          }
          break;
        case 'weekly':
          if (target.quarterly[indices[0]]?.monthly[indices[1]]?.weekly[indices[2]]) {
            target.quarterly[indices[0]].monthly[indices[1]].weekly[indices[2]] = {
              ...target.quarterly[indices[0]].monthly[indices[1]].weekly[indices[2]],
              completed: !target.quarterly[indices[0]].monthly[indices[1]].weekly[indices[2]].completed
            };
          }
          break;
        default:
          console.error(`Unknown level: ${level}`);
          return prevGoals;
      }

      return newGoals;
    });
  }, [currentCategory, setGoals]);

  const updateDailyGoal = useCallback((index, value) => {
    setDailyGoals(prev => {
      const newGoals = [...prev];
      newGoals[index] = value;
      return newGoals;
    });
  }, [setDailyGoals]);

  const toggleDailyGoalCompletion = useCallback((index) => {
    setDailyGoals(prev => {
      const newGoals = [...prev];
      newGoals[index] = newGoals[index].startsWith('‚úì ') ? newGoals[index].slice(2) : '‚úì ' + newGoals[index];
      return newGoals;
    });
  }, [setDailyGoals]);

  const clearDailyGoals = useCallback(() => {
    setDailyGoals(Array(14).fill(''));
  }, [setDailyGoals]);

  const startTimer = useCallback(() => {
    setIsTimerRunning(true);
    setRemainingTime(selectedDuration * 60);
  }, [selectedDuration]);

  useEffect(() => {
    let timer;
    if (isTimerRunning && remainingTime > 0) {
      timer = setInterval(() => {
        setRemainingTime(prevTime => {
          if (prevTime <= 1) {
            clearInterval(timer);
            setIsTimerRunning(false);
            alert("Time's up!");
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);
    } else if (remainingTime === 0) {
      setIsTimerRunning(false);
    }

    return () => {
      if (timer) clearInterval(timer);
    };
  }, [isTimerRunning, remainingTime]);

  const memoizedComponents = useMemo(() => ({
    categories: (
      <Categories
        categories={categories}
        currentCategory={currentCategory}
        setCurrentCategory={setCurrentCategory}
      />
    ),
    annualGoal: (
      <AnnualGoal
        annualGoal={goals[currentCategory]?.annual}
        updateGoal={updateGoal}
      />
    ),
    quarterlyGoals: (
      <QuarterlyGoals
        quarterlyGoals={goals[currentCategory]?.quarterly}
        updateGoal={updateGoal}
        toggleCompletion={toggleCompletion}
        setSelectedQuarter={setSelectedQuarter}
        selectedQuarter={selectedQuarter}
      />
    ),
    monthlyGoals: selectedQuarter !== null && (
      <MonthlyGoals
        monthlyGoals={goals[currentCategory]?.quarterly[selectedQuarter]?.monthly}
        updateGoal={updateGoal}
        toggleCompletion={toggleCompletion}
        setSelectedMonth={setSelectedMonth}
        selectedMonth={selectedMonth}
        selectedQuarter={selectedQuarter}
      />
    ),
    weeklyGoals: selectedQuarter !== null && selectedMonth !== null && (
      <WeeklyGoals
        weeklyGoals={goals[currentCategory]?.quarterly[selectedQuarter]?.monthly[selectedMonth]?.weekly}
        updateGoal={updateGoal}
        toggleCompletion={toggleCompletion}
        setSelectedWeek={setSelectedWeek}
        selectedWeek={selectedWeek}
        selectedQuarter={selectedQuarter}
        selectedMonth={selectedMonth}
      />
    ),
    dailyGoals: (
      <DailyGoals
        dailyGoals={dailyGoals}
        updateDailyGoal={updateDailyGoal}
        toggleDailyGoalCompletion={toggleDailyGoalCompletion}
        clearDailyGoals={clearDailyGoals}
      />
    ),
    workTimer: (
      <WorkTimer
        selectedDuration={selectedDuration}
        setSelectedDuration={setSelectedDuration}
        startTimer={startTimer}
        isTimerRunning={isTimerRunning}
        remainingTime={remainingTime}
      />
    )
  }), [goals, currentCategory, selectedQuarter, selectedMonth, selectedWeek, dailyGoals, updateGoal, toggleCompletion, updateDailyGoal, toggleDailyGoalCompletion, clearDailyGoals, selectedDuration, startTimer, isTimerRunning, remainingTime]);

  return (
    <div className={`container mx-auto p-4 ${darkMode ? 'dark-mode' : ''}`}>
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">OKR Tracker</h1>
        <button
          onClick={toggleDarkMode}
          className="px-4 py-2 bg-gray-200 dark:bg-gray-600 rounded-md"
        >
          {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
        </button>
      </div>

      {memoizedComponents.categories}

      <div className="mt-4">
        {memoizedComponents.annualGoal}
        {memoizedComponents.quarterlyGoals}
        {memoizedComponents.monthlyGoals}
        {memoizedComponents.weeklyGoals}
        {memoizedComponents.dailyGoals}
      </div>

      {memoizedComponents.workTimer}
    </div>
  );
};

export default App;
